namespace Shine
{
	/** 模型动作类型(generated by shine) */
	export class MotionConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:MotionConfig[];
		
		/** id */
		public id:number;
		
		/** 名字 */
		public name:string;
		
		/** 描述 */
		public describe:string;
		
		/** 使用名字 */
		public useName:string;
		
		/** 找不到动作的转化ID */
		public backID:number;
		
		/** 获取 */
		public static get(id:number):MotionConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:MotionConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:MotionConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():MotionConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.name=stream.readUTF();
			
			this.describe=stream.readUTF();
			
			this.useName=stream.readUTF();
			
			this.backID=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
