namespace Shine
{
	/** NPC表(generated by shine) */
	export class NPCConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,NPCConfig>;
		
		/** id */
		public id:number;
		
		/** 名字 */
		public name:string;
		
		/** 头像 */
		public icon:string;
		
		/** 模型ID */
		public modelID:number;
		
		/** 名字原值 */
		private _name:string;
		
		/** 获取 */
		public static get(id:number):NPCConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,NPCConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,NPCConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,NPCConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.name=stream.readUTF();
			
			this.icon=stream.readUTF();
			
			this.modelID=stream.readInt();
			
		}
		
		/** 生成刷新配置 */
		protected generateRefresh():void
		{
			if(this._name==null)
				this._name=this.name;
			this.name=LanguageConfig.getText(this._name);
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
