namespace Shine
{
	/** 平台表(generated by shine) */
	export class PlatformConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<string,PlatformConfig>;
		
		/** id */
		public id:string;
		
		/** 充值地址 */
		public chargeURL:string;
		
		/** 获取 */
		public static get(id:string):PlatformConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<string,PlatformConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<string,PlatformConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<string,PlatformConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readUTF();
			
			this.chargeURL=stream.readUTF();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
