namespace Shine
{
	/** 傀儡表(generated by shine) */
	export class PuppetConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,PuppetConfig>;
		
		/** id */
		public id:number;
		
		/** 战斗单位ID */
		public fightUnitID:number;
		
		/** AI行为方式 */
		public aiType:number;
		
		/** 是否客户端驱动 */
		public isClientDrive:boolean;
		
		/** 是否独立攻击者 */
		public isIndependentAttacker:boolean;
		
		/** 获取 */
		public static get(id:number):PuppetConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,PuppetConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,PuppetConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,PuppetConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.fightUnitID=stream.readInt();
			
			this.aiType=stream.readInt();
			
			this.isClientDrive=stream.readBoolean();
			
			this.isIndependentAttacker=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
