namespace Shine
{
	/** 傀儡等级表(generated by shine) */
	export class PuppetLevelConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,PuppetLevelConfig>;
		
		/** id */
		public id:number;
		
		/** 等级 */
		public level:number;
		
		/** 时间结束动作组 */
		public timeUpActions:number[][];
		
		/** 获取 */
		public static get(id:number,level:number):PuppetLevelConfig
		{
			return this._dic.get(id * 65536 + level);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,PuppetLevelConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,PuppetLevelConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,PuppetLevelConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.level=stream.readShort();
			
			var timeUpActionsLen:number=stream.readLen();
			this.timeUpActions=new Array<number[]>(timeUpActionsLen);
			var timeUpActionsT:number[][]=this.timeUpActions;
			for(var timeUpActionsI:number=0;timeUpActionsI<timeUpActionsLen;++timeUpActionsI)
			{
				var timeUpActionsV:number[];
				var timeUpActionsVLen:number=stream.readLen();
				timeUpActionsV=new Array<number>(timeUpActionsVLen);
				var timeUpActionsVT:number[]=timeUpActionsV;
				for(var timeUpActionsVI:number=0;timeUpActionsVI<timeUpActionsVLen;++timeUpActionsVI)
				{
					var timeUpActionsVV:number;
					timeUpActionsVV=stream.readInt();
					
					timeUpActionsVT[timeUpActionsVI]=timeUpActionsVV;
				}
				
				timeUpActionsT[timeUpActionsI]=timeUpActionsV;
			}
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
