namespace Shine
{
	/** 随机名字表(generated by shine) */
	export class RandomNameConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,RandomNameConfig>;
		
		/** 主键 */
		public id:number;
		
		/** 首名(姓) */
		public firstName:string;
		
		/** 次名(名) */
		public secondName:string;
		
		/** 组 */
		public group:number;
		
		/** 首名(姓)原值 */
		private _firstName:string;
		
		/** 次名(名)原值 */
		private _secondName:string;
		
		/** 获取 */
		public static get(id:number):RandomNameConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,RandomNameConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,RandomNameConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,RandomNameConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.firstName=stream.readUTF();
			
			this.secondName=stream.readUTF();
			
			this.group=stream.readInt();
			
		}
		
		/** 生成刷新配置 */
		protected generateRefresh():void
		{
			if(this._firstName==null)
				this._firstName=this.firstName;
			this.firstName=LanguageConfig.getText(this._firstName);
			
			if(this._secondName==null)
				this._secondName=this.secondName;
			this.secondName=LanguageConfig.getText(this._secondName);
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
