namespace Shine
{
	/** 玩家群等级表(generated by shine) */
	export class RoleGroupLevelConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,RoleGroupLevelConfig>;
		
		/** id */
		public id:number;
		
		/** 等级 */
		public level:number;
		
		/** 经验上限 */
		public expMax:number;
		
		/** 最多人数 */
		public maxNum:number;
		
		/** 获取 */
		public static get(id:number,level:number):RoleGroupLevelConfig
		{
			return this._dic.get(id * 4294967296 + level);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,RoleGroupLevelConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,RoleGroupLevelConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,RoleGroupLevelConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.level=stream.readInt();
			
			this.maxNum=stream.readInt();
			
			this.expMax=stream.readLong();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
