namespace Shine
{
	/** 玩家等级表(generated by shine) */
	export class RoleLevelConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,RoleLevelConfig>;
		
		/** 等级 */
		public level:number;
		
		/** 经验上限 */
		public expMax:number;
		
		/** 获取 */
		public static get(level:number):RoleLevelConfig
		{
			return this._dic.get(level);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,RoleLevelConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,RoleLevelConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,RoleLevelConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.level=stream.readInt();
			
			this.expMax=stream.readLong();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
