namespace Shine
{
	/** 场景特效表(generated by shine) */
	export class SceneEffectConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,SceneEffectConfig>;
		
		/** id */
		public id:number;
		
		/** 模型ID */
		public modelID:number;
		
		/** 特效ID */
		public effectID:number;
		
		/** 自动移除时间(未实现)(ms) */
		public autoRemoveTime:number;
		
		/** 获取 */
		public static get(id:number):SceneEffectConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,SceneEffectConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,SceneEffectConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,SceneEffectConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.modelID=stream.readInt();
			
			this.effectID=stream.readInt();
			
			this.autoRemoveTime=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
