namespace Shine
{
	/** 技能读条表(generated by shine) */
	export class SkillBarConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SMap<number,SkillBarConfig>;
		
		/** 读条id */
		public id:number;
		
		/** 读条类型 */
		public type:number;
		
		/** 动作id */
		public motionID:number;
		
		/** 读条时间(ms) */
		public time:number;
		
		/** 获取 */
		public static get(id:number):SkillBarConfig
		{
			return this._dic.get(id);
		}
		
		/** 设置字典 */
		public static setDic(dic:SMap<number,SkillBarConfig>):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SMap<number,SkillBarConfig>):void
		{
			this._dic.putAll(dic);
		}
		
		/** 获取全部 */
		public static getDic():SMap<number,SkillBarConfig>
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.type=stream.readInt();
			
			this.motionID=stream.readInt();
			
			this.time=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
