namespace Shine
{
	/** 界面逻辑体表(generated by shine) */
	export class UILogicConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:UILogicConfig[];
		
		/** 逻辑id */
		public id:number;
		
		/** 名字 */
		public name:string;
		
		/** 是否跟随父项显示 */
		public isFollowParentShow:boolean;
		
		/** 包名 */
		public packageName:string;
		
		/** 获取 */
		public static get(id:number):UILogicConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:UILogicConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:UILogicConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():UILogicConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.name=stream.readUTF();
			
			this.isFollowParentShow=stream.readBoolean();
			
			this.packageName=stream.readUTF();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
