namespace Shine
{
	/** 装备槽位类型(generated by shine) */
	export class EquipSlotTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:EquipSlotTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 对应装备类型 */
		public equipType:number;
		
		/** 是否默认开启 */
		public isOpen:boolean;
		
		/** 获取 */
		public static get(id:number):EquipSlotTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:EquipSlotTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:EquipSlotTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():EquipSlotTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.equipType=stream.readInt();
			
			this.isOpen=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
