namespace Shine
{
	/** gm类型(generated by shine) */
	export class GMTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:GMTypeConfig[];
		
		/** id */
		public id:number;
		
		/** gm指令权限 */
		public gmAble:boolean;
		
		/** repoter权限 */
		public repoterAble:boolean;
		
		/** 获取 */
		public static get(id:number):GMTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:GMTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:GMTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():GMTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.repoterAble=stream.readBoolean();
			
			this.gmAble=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
