namespace Shine
{
	/** 语言类型(generated by shine) */
	export class LanguageTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:LanguageTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 提示语 */
		public showText:string;
		
		/** 是否屏蔽 */
		public disable:boolean;
		
		/** 获取 */
		public static get(id:number):LanguageTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:LanguageTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:LanguageTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():LanguageTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.showText=stream.readUTF();
			
			this.disable=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
