namespace Shine
{
	/** 邮件类型(generated by shine) */
	export class MailTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:MailTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 领取后是否自动删除 */
		public isDeleteAfterTake:boolean;
		
		/** 获取 */
		public static get(id:number):MailTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:MailTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:MailTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():MailTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.isDeleteAfterTake=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
