namespace Shine
{
	/** 模型绑点位置类型(generated by shine) */
	export class ModelBindPosTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:ModelBindPosTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 使用名 */
		public useName:string;
		
		/** 获取 */
		public static get(id:number):ModelBindPosTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:ModelBindPosTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:ModelBindPosTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():ModelBindPosTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.useName=stream.readUTF();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
