namespace Shine
{
	/** 角色显示数据部件类型(generated by shine) */
	export class RoleShowDataPartTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:RoleShowDataPartTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 是否为社交字段 */
		public isSocialPart:boolean;
		
		/** 获取 */
		public static get(id:number):RoleShowDataPartTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:RoleShowDataPartTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:RoleShowDataPartTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():RoleShowDataPartTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.isSocialPart=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
