namespace Shine
{
	/** 场景类型(generated by shine) */
	export class SceneTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SceneTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 是否需要场景角色 */
		public needRole:boolean;
		
		/** 场景实例类型 */
		public instanceType:number;
		
		/** 获取 */
		public static get(id:number):SceneTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:SceneTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SceneTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():SceneTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.instanceType=stream.readInt();
			
			this.needRole=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
