namespace Shine
{
	/** 技能影响类型(generated by shine) */
	export class SkillInfluenceTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:SkillInfluenceTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 默认值 */
		public defaultValue:boolean;
		
		/** 获取 */
		public static get(id:number):SkillInfluenceTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:SkillInfluenceTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:SkillInfluenceTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():SkillInfluenceTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.defaultValue=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
