namespace Shine
{
	/** 任务目标类型(generated by shine) */
	export class TaskTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:TaskTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 是否客户端驱动 */
		public isClientDrive:boolean;
		
		/** 是否刷新式目标 */
		public needUpdate:boolean;
		
		/** 是否需要二号第二索引 */
		public needSecondKey2:boolean;
		
		/** 是否需要第二索引 */
		public needSecondKey:boolean;
		
		/** 是否需要自定义数据结构 */
		public needCustomTask:boolean;
		
		/** 获取 */
		public static get(id:number):TaskTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:TaskTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:TaskTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():TaskTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.isClientDrive=stream.readBoolean();
			
			this.needUpdate=stream.readBoolean();
			
			this.needSecondKey=stream.readBoolean();
			
			this.needSecondKey2=stream.readBoolean();
			
			this.needCustomTask=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
