namespace Shine
{
	/** 单位移动方式(generated by shine) */
	export class UnitMoveTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:UnitMoveTypeConfig[];
		
		/** id */
		public id:number;
		
		/** 使用速度比率 */
		public radio:number;
		
		/** 动作id */
		public motionID:number;
		
		/** 获取 */
		public static get(id:number):UnitMoveTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:UnitMoveTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:UnitMoveTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():UnitMoveTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.radio=stream.readFloat();
			
			this.motionID=stream.readInt();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
