namespace Shine
{
	/** 单位特殊移动类型(generated by shine) */
	export class UnitSpecialMoveTypeConfig extends BaseConfig
	{
		/** 存储集合 */
		private static _dic:UnitSpecialMoveTypeConfig[];
		
		/** 是否附加眩晕效果 */
		public withVertigo:boolean;
		
		/** id */
		public id:number;
		
		/** 获取 */
		public static get(id:number):UnitSpecialMoveTypeConfig
		{
			return id>=0 && id<this._dic.length ? this._dic[id] : null;
		}
		
		/** 设置字典 */
		public static setDic(dic:UnitSpecialMoveTypeConfig[]):void
		{
			this._dic=dic;
		}
		
		/** 添加字典(热更用) */
		public static addDic(dic:UnitSpecialMoveTypeConfig[]):void
		{
			ObjectUtils.arrayPutAll(this._dic,dic);
		}
		
		/** 获取全部 */
		public static getDic():UnitSpecialMoveTypeConfig[]
		{
			return this._dic;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
			this.withVertigo=stream.readBoolean();
			
		}
		
		/** 读完所有表后处理 */
		public static afterReadConfigAll():void
		{
			
		}
		
	}
}
