namespace Shine
{
	/** 攻击行为类型(generated by shine) */
	export class AttackActionType
	{
		/** 攻击 arg1:攻击ID arg2:攻击等级 */
		public static Attack:number=1;
		
		/** 子弹 arg1:子弹等级 arg2:子弹等级 */
		public static Bullet:number=2;
		
		/** 自身添加固定属性 arg1:属性类型,arg2:属性值 */
		public static SelfAddAttribute:number=3;
		
		/** 目标添加固定属性 arg1:属性类型,arg2:属性值 */
		public static TargetAddAttribute:number=4;
		
		/** 自身添加变量属性 arg1:属性类型,arg2:变量ID */
		public static SelfAddAttributeVar:number=5;
		
		/** 目标添加变量属性 arg1:属性类型,arg2:变量ID */
		public static TargetAddAttributeVar:number=6;
		
		/** 自身特殊移动 arg1:特殊移动ID */
		public static SelfSpecialMove:number=7;
		
		/** 目标特殊移动 arg1:特殊移动ID */
		public static TargetSpecialMove:number=8;
		
		/** 自身添加buff arg1:id arg2:level */
		public static SelfAddBuff:number=9;
		
		/** 目标添加buff arg1:id arg2:level */
		public static TargetAddBuff:number=10;
		
		/** 自身增加当前组CD时间经过 arg1:组ID arg2:时间(ms) */
		public static AddGroupTimePass:number=11;
		
		/** 自身移除组CD arg1:组ID */
		public static RemoveGroupCD:number=12;
		
		/** 在目标点创建傀儡 arg1:傀儡ID arg2:傀儡等级 */
		public static AddPuppetAtTarget:number=13;
		
		/** 自杀 */
		public static KillSelf:number=14;
		
		/** 击杀目标 */
		public static KillTarget:number=15;
		
		/** 直接移除某组buff arg1:组ID */
		public static SelfRemoveGroupBuff:number=16;
		
		/** 目标移除某组buff arg1:组ID */
		public static TargetRemoveGroupBuff:number=17;
		
		/** 长度 */
		public static size:number=18;
		
	}
}
