namespace Shine
{
	/** 攻击判定阶段(generated by shine) */
	export class AttackMomentType
	{
		/** 攻击时命中前(无论是否命中)(每次attack触发一次) */
		public static AttackBeforeHit:number=1;
		
		/** 攻击时出伤害前(主目标执行一次) */
		public static AttackBeforeDamageMainTarget:number=2;
		
		/** 攻击时出伤害后(主目标执行一次)(此时能取到伤害值) */
		public static AttackAfterDamageMainTarget:number=3;
		
		/** 攻击时击中任意目标时(出伤害前)(每个attack触发一次) */
		public static AttackOnHitAnyTarget:number=4;
		
		/** 攻击时出伤害前(每个目标执行一次) */
		public static AttackBeforeDamageEachTarget:number=5;
		
		/** 攻击时出伤害后(每个目标执行一次)(此时能取到伤害值) */
		public static AttackAfterDamageEachTarget:number=6;
		
		/** 攻击时命中后(无论是否命中)(每次attack触发一次) */
		public static AttackAfterHit:number=7;
		
		/** 被攻击时出命中前 */
		public static BeAttackBeforeHit:number=8;
		
		/** 被攻击命中时出伤害前 */
		public static BeAttackBeforeDamage:number=9;
		
		/** 被攻击命中时出伤害后(此时能取到伤害值) */
		public static BeAttackAfterDamage:number=10;
		
		/** 击杀后(目标死亡但存在) */
		public static AfterKill:number=11;
		
		/** 被击杀前 */
		public static BeforeBeKill:number=12;
		
		/** 子弹攻击时命中前(无论是否命中)(子弹的attack命中第一个目标时) */
		public static AttackBeforeHitForBulletFirstHit:number=13;
		
		/** 长度 */
		public static size:number=14;
		
	}
}
