namespace Shine
{
	/** 属性公式类型(generated by shine) */
	export class AttributeFormulaType
	{
		/** 单值 arg1 */
		public static Single:number=1;
		
		/** 默认2项 arg1*r(arg2) */
		public static Normal2:number=2;
		
		/** 默认3项 arg1*r(arg2)+arg3 */
		public static Normal3:number=3;
		
		/** 默认4项 (arg1*r(arg2)+arg3)*r(arg4) */
		public static Normal4:number=4;
		
		/** 两项和 arg1+arg2 */
		public static TwoPlus:number=5;
		
		/** 长度 */
		public static size:number=6;
		
	}
}
