namespace Shine
{
	/** 子弹投掷类型(generated by shine) */
	export class BulletCastType
	{
		/** 立即 */
		public static Immediately:number=1;
		
		/** 固定时间,锁定目标单位 arg0:时间(ms) */
		public static LockTargetByTime:number=2;
		
		/** 固定时间,锁定目标地面 arg0:时间(ms) */
		public static LockGroundByTime:number=3;
		
		/** 固定速度追击,锁定目标单位 arg0:速度  */
		public static LockTargetBySpeed:number=4;
		
		/** 固定速度追击,锁定目标地面 arg0:速度  */
		public static LockGroundBySpeed:number=5;
		
		/** 直线碰撞 arg0:速度 arg1:最大距离 arg2:碰撞半径 */
		public static HitStraight:number=6;
		
		/** 直线碰撞拓展 arg0:速度变量ID(千分位) arg1:最大距离变量ID(千分位) arg2:碰撞半径 */
		public static HitStraightEx:number=7;
		
		/** 直线显示,纯客户端表现用 arg0:速度 arg1:最大距离 */
		public static StraightForShow:number=8;
		
		/** 自身范围碰撞 arg0:时间(ms) arg1:碰撞半径 */
		public static HitBySelf:number=9;
		
		/** 自身范围碰撞圆形 arg0:时间(ms) arg1:碰撞半径 */
		public static HitSelfCircle:number=9;
		
		/** 自身范围碰撞矩形 arg0:时间(ms) arg1:碰撞长(纵向) arg2:碰撞宽(横向) */
		public static HitSelfRect:number=10;
		
		/** 长度 */
		public static size:number=11;
		
	}
}
