namespace Shine
{
	/** 流程步骤类型(generated by shine) */
	export class FlowStepType
	{
		/** 客户端http登录(LoginMainControl.userLogin) */
		public static ClientLoginHttp:number=10;
		
		/** 客户端http登录回复 */
		public static ClientLoginHttpReBack:number=20;
		
		/** 用户预备登录(消息到达game服) */
		public static PreUserLogin:number=50;
		
		/** 回复客户端用户预备登录 */
		public static RePreUserLogin:number=4;
		
		/** 用户登录(服务器收到客户端LoginGame消息) */
		public static UserLogin:number=90;
		
		/** 回复角色列表 */
		public static RePlayerList:number=100;
		
		/** 创建角色(收到创建角色消息) */
		public static CreatePlayer:number=110;
		
		/** 角色登录 */
		public static PlayerLogin:number=130;
		
		/** 初始化客户端 */
		public static InitClient:number=140;
		
		/** 开始选择服务器列表(分区服的模式有) */
		public static BeginSelectServer:number=30;
		
		/** 选择区服结束，发送消息到服务器 */
		public static SelectServerOver:number=40;
		
		/** 服务器回复game服信息 */
		public static ReGameServerInfo:number=60;
		
		/** 客户端开始连接game服 */
		public static StartConnectGame:number=70;
		
		/** 连接game服成功并发送登录消息 */
		public static ConnectGameSuccess:number=80;
		
		/** 创建角色成功(回复客户端消息) */
		public static CreatePlayerSuccess:number=120;
		
		/** 长度 */
		public static size:number=0;
		
	}
}
