namespace Shine
{
	/** 怪物掉落类型(generated by shine) */
	export class MonsterDropType
	{
		/** 无掉落 */
		public static None:number=0;
		
		/** 直接添加(包满无法再添加) */
		public static Add:number=1;
		
		/** 直接添加(包满邮件) */
		public static AddAbs:number=2;
		
		/** 物品类掉落(真) */
		public static Drop:number=3;
		
		/** 物品类走掉落包(真) */
		public static DropBag:number=4;
		
		/** 物品类掉落(仅自己可见) */
		public static SelfDrop:number=5;
		
		/** 物品类走掉落包(仅自己可见) */
		public static SelfDropBag:number=6;
		
		/** 仅自己可见掉落包，绑定单位(尸体) */
		public static SelfDropBagBindUnit:number=7;
		
		/** 长度 */
		public static size:number=8;
		
	}
}
