namespace Shine
{
	/** 模型动作类型(generated by shine) */
	export class MotionType
	{
		/** 地面待机 */
		public static Idle:number=1;
		
		/** 走 */
		public static Walk:number=2;
		
		/** 跑 */
		public static Run:number=3;
		
		/** 倒地 */
		public static Lie:number=4;
		
		/** 起跳 */
		public static PreJump:number=5;
		
		/** 跳跃上飞 */
		public static JumpUp:number=6;
		
		/** 跳跃下落 */
		public static JumpDown:number=7;
		
		/** 空中待机 */
		public static FlyIdle:number=8;
		
		/** 空中上飞 */
		public static FlyUp:number=9;
		
		/** 空中下落 */
		public static FlyDown:number=10;
		
		/** 地面受击1 */
		public static Hurt1:number=11;
		
		/** 地面受击2 */
		public static Hurt2:number=12;
		
		/** 地面僵直1(通眩晕) */
		public static Spasticity1:number=13;
		
		/** 地面僵直2 */
		public static Spasticity2:number=14;
		
		/** 受击浮空上飞 */
		public static BlowUp:number=15;
		
		/** 受击浮空下落 */
		public static BlowDown:number=16;
		
		/** 死亡过程 */
		public static Dead:number=17;
		
		/** 长度 */
		public static size:number=18;
		
	}
}
