namespace Shine
{
	/** 任务重复类型(generated by shine) */
	export class QuestRepeatType
	{
		/** 可重复(临时/随机任务类) */
		public static Repeat:number=0;
		
		/** 单次(主线/支线累) */
		public static Once:number=1;
		
		/** 周期任务(日常类) */
		public static Cycle:number=2;
		
		/** 长度 */
		public static size:number=3;
		
	}
}
