namespace Shine
{
	/** 随机物品方式(generated by shine) */
	export class RandomItemType
	{
		/** 单个物品(id,[num]) */
		public static Single:number=1;
		
		/** 等权物品组(id,[num];id,[num]) */
		public static AverageItem:number=2;
		
		/** 权重随机物品组(weight,id,[num];weight,id,[num],id,[num]) */
		public static WeightItem:number=3;
		
		/** 权重随机创建物品组(weight,createID,[num];weight,createID,[num]) */
		public static WeightCreateItem:number=4;
		
		/** 权重随机随机物品组(嵌套)(weight,randomItemID;weight,randomItemID) */
		public static WeightRandomItem:number=5;
		
		/** 长度 */
		public static size:number=6;
		
	}
}
