namespace Shine
{
	/** 角色显示数据部件类型(generated by shine) */
	export class RoleShowDataPartType
	{
		/** 名字(string) */
		public static Name:number=1;
		
		/** 性别(int) */
		public static Sex:number=2;
		
		/** 职业(int) */
		public static Vocation:number=3;
		
		/** 等级(int) */
		public static Level:number=4;
		
		/** 是否在线(int==1)(RoleSocial用) */
		public static IsOnline:number=5;
		
		/** 离线时间 */
		public static LastOnlineTime:number=6;
		
		/** 战斗力(long) */
		public static FightForce:number=7;
		
		/** 工会(unionID+unionName) */
		public static Union:number=8;
		
		/** 场景位置(SceneLocationRoleShowChangeData) */
		public static Location:number=9;
		
		/** 长度 */
		public static size:number=10;
		
	}
}
