namespace Shine
{
	/** 场景实例类型(generated by shine) */
	export class SceneInstanceType
	{
		/** 单例场景(主城) */
		public static SingleInstance:number=1;
		
		/** 单人副本(可随时出，与player绑定,mmo多用) */
		public static SinglePlayerBattle:number=2;
		
		/** 多人副本(可随时出，与player绑定,mmo多用) */
		public static MultiPlayerBattle:number=3;
		
		/** 副本预进入场景(限定进入,与player绑定,moba准备场景用) */
		public static PreBattle:number=4;
		
		/** 限定多人副本(不可随时出，不与player绑定) */
		public static FiniteBattle:number=5;
		
		/** 限定多人副本(不可随时出，不与player绑定,帧同步) */
		public static FiniteBattleWithFrameSync:number=6;
		
		/** 分线单例场景(主城) */
		public static LinedSingleInstance:number=7;
		
		/** 客户端驱动的单人副本 */
		public static ClientDriveSinglePlayerBattle:number=8;
		
		/** 自动分线的单例场景(主城类) */
		public static AutoLinedScene:number=9;
		
		/** 玩家群独立场景 */
		public static RoleGroupScene:number=10;
		
		/** 长度 */
		public static size:number=11;
		
	}
}
