namespace Shine
{
	/** 技能行为类型(generated by shine) */
	export class SkillActionType
	{
		/** 特殊移动 arg1:移动类型 arg2:移动参数 */
		public static SpecialMove:number=1;
		
		/** 直接给自身加buff arg1:buffID arg2:buffLevel */
		public static SelfAddBuff:number=2;
		
		/** 在目标点创建傀儡 arg1:傀儡ID arg2:傀儡等级 [arg3:存在时间(ms)(0为无限)] */
		public static AddPuppetAtTarget:number=3;
		
		/** 直接给自身加固定属性 arg1:属性类型 arg2:属性值 */
		public static SelfAddAttribute:number=4;
		
		/** 直接给自身加变量属性 arg1:属性类型 arg2:变量ID */
		public static SelfAddAttributeVar:number=5;
		
		/** 自杀 */
		public static KillSelf:number=6;
		
		/** 自身增加当前组CD时间经过 arg1:组ID arg2:时间(ms) */
		public static AddGroupTimePass:number=7;
		
		/** 自身移除组CD arg1:组ID */
		public static RemoveGroupCD:number=8;
		
		/** 创建选择的建筑(从SkillTargetData的arg读建筑id) [arg1:是否直接就绪] */
		public static CreateSelectBuilding:number=9;
		
		/** 直接移除某组buff arg1:组ID */
		public static SelfRemoveGroupBuff:number=10;
		
		/** 拾取掉落物品(目标为掉落物品时) */
		public static PickFieldItem:number=11;
		
		/** 使用操作体(目标为操作体时) */
		public static Operate:number=12;
		
		/** 长度 */
		public static size:number=0;
		
	}
}
