namespace Shine
{
	/** 技能伤害类型(generated by shine) */
	export class SkillDamageType
	{
		/** 物理伤害 */
		public static PhysicsDamage:number=1;
		
		/** 魔法伤害 */
		public static MagicDamage:number=2;
		
		/** 神圣伤害(直接伤害) */
		public static HolyDamage:number=3;
		
		/** 回血 */
		public static HpAdd:number=4;
		
		/** 回蓝 */
		public static MpAdd:number=5;
		
		/** 减蓝 */
		public static MpSub:number=6;
		
		/** 长度 */
		public static size:number=7;
		
	}
}
