namespace Shine
{
	/** 技能步骤切换类型(generated by shine) */
	export class SkillStepSwitchType
	{
		/** 自动切换(动作结束后)(arg1:下个step) */
		public static Auto:number=1;
		
		/** 技能指令控制(arg1:下个step,[arg2:切换时间],[arg3:开始接收时间],[arg4:结束接收时间]) */
		public static SkillCommand:number=2;
		
		/** 长度 */
		public static size:number=3;
		
	}
}
