namespace Shine
{
	/** 技能目标类型(generated by shine) */
	export class SkillTargetType
	{
		/** 无目标(立即释放)(无视距离限制) */
		public static None:number=0;
		
		/** 任意目标(单体) */
		public static Single:number=1;
		
		/** 地面坐标 */
		public static Ground:number=2;
		
		/** 朝向 */
		public static Direction:number=3;
		
		/** 攻击范围判定(矩形)(但是先要符合距离限制) */
		public static AttackScope:number=4;
		
		/** 长度 */
		public static size:number=5;
		
	}
}
