namespace Shine
{
	/** 状态类型表(generated by shine) */
	export class StatusType
	{
		/** 死亡 */
		public static IsDead:number=1;
		
		/** 处于战斗状态 */
		public static OnFight:number=2;
		
		/** 不可被选择(蝗虫群) */
		public static CantBeTarget:number=3;
		
		/** 不可移动 */
		public static CantMove:number=4;
		
		/** 不可转身 */
		public static CantTurn:number=5;
		
		/** 不可攻击 */
		public static CantAttack:number=6;
		
		/** 不可施法 */
		public static CantCast:number=7;
		
		/** 眩晕(等价于以上4个) */
		public static Vertigo:number=8;
		
		/** 控制免疫 */
		public static ControlImmun:number=9;
		
		/** 伤害免疫 */
		public static DamageImmun:number=10;
		
		/** 敌对不可选(无敌) */
		public static CantBeEnemyTarget:number=11;
		
		/** 完整无敌(等价于以上3个加不可被伤害击杀) */
		public static Invincible:number=12;
		
		/** 绝对命中(克制绝对闪避) */
		public static AbsHit:number=13;
		
		/** 绝对闪避 */
		public static AbsDodge:number=14;
		
		/** 绝对暴击 */
		public static AbsCrit:number=15;
		
		/** 绝对韧性(克制绝对暴击) */
		public static AbsTenacity:number=16;
		
		/** 隐身 */
		public static Hide:number=17;
		
		/** 安全区(mmo) */
		public static SafeRegion:number=18;
		
		/** 吸收子弹(盾墙) */
		public static AbsorbBullet:number=19;
		
		/** 不可被攻击(attack跳过) */
		public static CantBeAttackTarget:number=20;
		
		/** 不可被伤害击杀(暗牧-薄葬,蛮王大) */
		public static CantBeKillByDamage:number=21;
		
		/** 不可被治疗 */
		public static CantBeHeal:number=22;
		
		/** 长度 */
		public static size:number=23;
		
	}
}
