namespace Shine
{
	/** 程序文本枚举(generated by shine) */
	export class TextEnum
	{
		/** 网络未开启 */
		public static NetNotOpen:string;
		
		/** 服务器未响应 */
		public static ServerNotRespond:string;
		
		/** 网络连接被断开 */
		public static SocketClosed:string;
		
		/** 需要下载新的更新包 */
		public static NeedHotfix:string;
		
		/** 从配置读取 */
		public static readConfig():void
		{
			this.NetNotOpen=TextConfig.getText("netNotOpen");
			this.ServerNotRespond=TextConfig.getText("serverNotRespond");
			this.SocketClosed=TextConfig.getText("socketClosed");
			this.NeedHotfix=TextConfig.getText("needHotfix");
		}
		
	}
}
