namespace Shine
{
	/** UI显示隐藏时刻类型(generated by shine) */
	export class UIDoMomentType
	{
		/** 离开场景时隐藏 */
		public static RemoveSceneHide:number=0;
		
		/** 离开场景完毕时隐藏 */
		public static RemoveSceneOverHide:number=1;
		
		/** 进入场景时显示  */
		public static EnterSceneShow:number=2;
		
		/** 进入场景完毕时显示  */
		public static EnterSceneOverShow:number=3;
		
		/** 离开场景(无场景)时显示  */
		public static LeaveSceneOverShow:number=4;
		
		/** 长度 */
		public static size:number=5;
		
	}
}
