namespace Shine
{
	/** 单位AI指令类型(generated by shine) */
	export class UnitAICommandType
	{
		/** 无 */
		public static None:number=0;
		
		/** 保护原地(怪物默认) */
		public static Protect:number=1;
		
		/** 移动到 */
		public static MoveTo:number=3;
		
		/** 特殊移动到 */
		public static SpecialMoveTo:number=4;
		
		/** 攻击到 */
		public static AttackTo:number=5;
		
		/** 技能攻击到 */
		public static SkillTo:number=6;
		
		/** 攻击移动到(war3) */
		public static AttackMoveTo:number=7;
		
		/** 拾取物品 */
		public static PickUpItem:number=8;
		
		/** 朝向移动 */
		public static MoveDir:number=9;
		
		/** 原地(遇敌对逃跑) */
		public static Escape:number=2;
		
		/** 长度 */
		public static size:number=10;
		
	}
}
