namespace Shine
{
	/** 单位基元移动类型(generated by shine) */
	export class UnitBaseMoveState
	{
		/** 没有移动 */
		public static None:number=0;
		
		/** 定向移动 */
		public static MoveToDir:number=1;
		
		/** 定点移动 */
		public static MoveToPos:number=2;
		
		/** 特殊移动中 */
		public static SpecialMove:number=3;
		
		/** 长度 */
		public static size:number=4;
		
	}
}
