namespace Shine
{
	/** 单位复活类型(generated by shine) */
	export class UnitReviveType
	{
		/** 不复活(死亡完成后_立即移除) */
		public static Remove:number=0;
		
		/** 立刻复活(满血蓝) */
		public static Revive:number=1;
		
		/** 自定义(保持死亡状态不处理) */
		public static Custom:number=2;
		
		/** 死亡完成后立刻复活(满血蓝) */
		public static ReviveAtDeadOver:number=1;
		
		/** 复活时间结束后立刻复活(只有怪物类型可用) */
		public static ReviveAtTime:number=3;
		
		/** 长度 */
		public static size:number=4;
		
	}
}
