namespace Shine
{
	/** 单位类型(generated by shine) */
	export class UnitType
	{
		/** 角色 */
		public static Character:number=1;
		
		/** 怪物 */
		public static Monster:number=2;
		
		/** NPC */
		public static Npc:number=3;
		
		/** 伙伴(宠物) */
		public static Partner:number=4;
		
		/** 傀儡(辅助施法单位)(召唤单位) */
		public static Puppet:number=5;
		
		/** 载具 */
		public static Vehicle:number=6;
		
		/** 场景特效 */
		public static SceneEffect:number=7;
		
		/** 操作体(不可被攻击) */
		public static Operation:number=8;
		
		/** 掉落物品 */
		public static FieldItem:number=9;
		
		/** 建筑物 */
		public static Building:number=10;
		
		/** 掉落物品包 */
		public static FieldItemBag:number=11;
		
		/** 长度 */
		public static size:number=12;
		
	}
}
