namespace Shine
{
	/** 事务状态(generated by shine) */
	export class WorkState
	{
		/** 空闲 */
		public static None:number=0;
		
		/** 执行中 */
		public static Doing:number=1;
		
		/** 已完成 */
		public static Completed:number=2;
		
		/** 执行失败 */
		public static Failed:number=3;
		
		/** 长度 */
		public static size:number=4;
		
	}
}
