namespace Shine
{
	/** 活动服务器保存数据(generated by shine) */
	export class ActivityServerData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.ActivityServer;
		
		/** 活动id */
		public id:number;
		
		/** 是否运行中(存库时 无效) */
		public isRunning:boolean;
		
		/** 下次重置时间 */
		public nextResetTime:number=-1;
		
		/** 下次触发时间 */
		public nextTurnTime:number;
		
		/** 上次触发时间 */
		public lastTurnTime:number;
		
		/** 是否强制关闭了 */
		public isForceClosed:boolean;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.ActivityServer;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ActivityServerData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.isRunning=stream.readBoolean();
			
			this.nextResetTime=stream.readLong();
			
			this.isForceClosed=stream.readBoolean();
			
			this.lastTurnTime=stream.readLong();
			
			this.nextTurnTime=stream.readLong();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeBoolean(this.isRunning);
			
			stream.writeLong(this.nextResetTime);
			
			stream.writeBoolean(this.isForceClosed);
			
			stream.writeLong(this.lastTurnTime);
			
			stream.writeLong(this.nextTurnTime);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof ActivityServerData))
				return;
			
			var mData:ActivityServerData=data as ActivityServerData;
			
			this.id=mData.id;
			this.isRunning=mData.isRunning;
			this.nextResetTime=mData.nextResetTime;
			this.isForceClosed=mData.isForceClosed;
			this.lastTurnTime=mData.lastTurnTime;
			this.nextTurnTime=mData.nextTurnTime;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof ActivityServerData))
				return;
			
			var mData:ActivityServerData=data as ActivityServerData;
			
			this.id=mData.id;
			
			this.isRunning=mData.isRunning;
			
			this.nextResetTime=mData.nextResetTime;
			
			this.isForceClosed=mData.isForceClosed;
			
			this.lastTurnTime=mData.lastTurnTime;
			
			this.nextTurnTime=mData.nextTurnTime;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
