namespace Shine
{
	/** 玩家邮件(generated by shine) */
	export class PlayerMailData extends MailData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.PlayerMail;
		
		/** 角色数据 */
		public roleData:RoleShowData;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.PlayerMail;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PlayerMailData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.roleData=stream.readDataSimpleNotNull() as RoleShowData;
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeDataSimpleNotNull(this.roleData);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof PlayerMailData))
				return;
			
			var mData:PlayerMailData=data as PlayerMailData;
			
			this.roleData=mData.roleData;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof PlayerMailData))
				return;
			
			var mData:PlayerMailData=data as PlayerMailData;
			
			this.roleData=mData.roleData.clone() as RoleShowData;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
			this.roleData=new RoleShowData();
			this.roleData.initDefault();
		}
		
	}
}
