namespace Shine
{
	/** 客户端登录缓存数据(generated by shine) */
	export class ClientLoginCacheData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.ClientLoginCache;
		
		/** uid */
		public uid:string;
		
		/** 平台类型 */
		public platform:string;
		
		/** 区服ID */
		public areaID:number=-1;
		
		/** 服务器生成码 */
		public serverBornCode:number;
		
		/** 上次登录角色ID */
		public lastPlayerID:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.ClientLoginCache;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ClientLoginCacheData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.uid=stream.readUTF();
			
			this.platform=stream.readUTF();
			
			this.areaID=stream.readInt();
			
			this.lastPlayerID=stream.readLong();
			
			this.serverBornCode=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeUTF(this.uid);
			
			stream.writeUTF(this.platform);
			
			stream.writeInt(this.areaID);
			
			stream.writeLong(this.lastPlayerID);
			
			stream.writeInt(this.serverBornCode);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof ClientLoginCacheData))
				return;
			
			var mData:ClientLoginCacheData=data as ClientLoginCacheData;
			
			this.uid=mData.uid;
			this.platform=mData.platform;
			this.areaID=mData.areaID;
			this.lastPlayerID=mData.lastPlayerID;
			this.serverBornCode=mData.serverBornCode;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof ClientLoginCacheData))
				return;
			
			var mData:ClientLoginCacheData=data as ClientLoginCacheData;
			
			this.uid=mData.uid;
			
			this.platform=mData.platform;
			
			this.areaID=mData.areaID;
			
			this.lastPlayerID=mData.lastPlayerID;
			
			this.serverBornCode=mData.serverBornCode;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
