namespace Shine
{
	/** 客户端登录扩展数据(generated by shine) */
	export class ClientLoginExData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.ClientLoginEx;
		
		/** 数据 */
		public data:ClientLoginData;
		
		/** 账号唯一ID */
		public userID:number;
		
		/** ip地址 */
		public ip:string;
		
		/** 选择区服ID */
		public areaID:number=-1;
		
		/** 是否成人 */
		public isAdult:boolean;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.ClientLoginEx;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ClientLoginExData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.data=stream.readDataSimpleNotNull() as ClientLoginData;
			
			this.userID=stream.readLong();
			
			this.ip=stream.readUTF();
			
			this.isAdult=stream.readBoolean();
			
			this.areaID=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeDataSimpleNotNull(this.data);
			
			stream.writeLong(this.userID);
			
			stream.writeUTF(this.ip);
			
			stream.writeBoolean(this.isAdult);
			
			stream.writeInt(this.areaID);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof ClientLoginExData))
				return;
			
			var mData:ClientLoginExData=data as ClientLoginExData;
			
			this.data=mData.data;
			this.userID=mData.userID;
			this.ip=mData.ip;
			this.isAdult=mData.isAdult;
			this.areaID=mData.areaID;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof ClientLoginExData))
				return;
			
			var mData:ClientLoginExData=data as ClientLoginExData;
			
			this.data=mData.data.clone() as ClientLoginData;
			
			this.userID=mData.userID;
			
			this.ip=mData.ip;
			
			this.isAdult=mData.isAdult;
			
			this.areaID=mData.areaID;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			this.data=new ClientLoginData();
			this.data.initDefault();
		}
		
	}
}
