namespace Shine
{
	/** 客户端登陆服务器地址信息(generated by shine) */
	export class ClientLoginServerInfoData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.ClientLoginServerInfo;
		
		/** 令牌 */
		public token:number;
		
		/** 地址 */
		public host:string;
		
		/** 端口 */
		public port:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.ClientLoginServerInfo;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.token=stream.readInt();
			
			this.host=stream.readUTF();
			
			this.port=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.token);
			
			stream.writeUTF(this.host);
			
			stream.writeInt(this.port);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof ClientLoginServerInfoData))
				return;
			
			var mData:ClientLoginServerInfoData=data as ClientLoginServerInfoData;
			
			this.token=mData.token;
			this.host=mData.host;
			this.port=mData.port;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof ClientLoginServerInfoData))
				return;
			
			var mData:ClientLoginServerInfoData=data as ClientLoginServerInfoData;
			
			this.token=mData.token;
			
			this.host=mData.host;
			
			this.port=mData.port;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
